acfcreateoptions.body:
// Add ACF Menu
if ( function_exists( "acf_add_options_page" ) ) {
  acf_add_options_page();
}

// Add Specific ACF Menu Pages
if ( function_exists( "register_options_page" ) ) {
  register_options_page( 'Global Options' );
  register_options_page( 'Another Options Page' );
}

acffirstrepeater.body:
// Get First Repeater Row
$first_row = true;
if ( have_rows( '${1:field_name}' ) ) : ?>
  <?php while ( $first_row && have_rows( '${1:field_name}' ) ) : the_row(); ?>
    <${2:div}><?php the_sub_field( '${3:sub_field_name}' ); ?></${2:div}>
    <?php $first_row = false;
  endwhile; ?>
<?php endif;

ffall.body:
// Start Flexible Content
if ( have_rows( '${1:field_name}' ) ) : ?>
	<?php while ( have_rows('${1:field_name}' ) ) : the_row();

		// Layout Name: ${2:layout_name}
		if ( get_row_layout() == '${3:layout_field}' ) : ?>
			<${4:div}>
				<?php the_sub_field( '${5:field_name}' ); ?>
			</${4:div}>

		<?php endif; ?>
	<?php endwhile; ?>
<?php endif;

gmf.body:
// ACF Google Map
$location = get_field( '${1:field_name}' );
if ( ! empty($location) ) : ?>

<div class="acf-map">
  <div class="marker" data-lat="<?php echo $location['lat']; ?>" data-lng="<?php echo $location['lng']; ?>"></div>
</div>

<?php endif;

